package day0927;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y;
		x = 5;
		y = 2;
		System.out.println(x + y);
		System.out.println(x - y);
		System.out.println(x * y);
		System.out.println(x / y);
		System.out.println(x % y);
		
		System.out.println("--------------------");
		System.out.println(5 + 2);
		System.out.println("5 + 2");
		System.out.println(5 - 2);
		System.out.println(5 * 2);
		System.out.println(5 / 2);
		System.out.println(5 % 2);
	}

}
