package day0927;

public class ComplexSubstitutionOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum = 0, i = 1;
		
		sum += i;
		System.out.println("sum : " + sum);
		
		i++;
		sum += i;
		System.out.println("sum : " + sum);
		
		i++;
		sum += i;
		System.out.println("sum : " + sum);
	}

}
