package day1005;

public class ArrayFor2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 배열 선언 및 생성
		int[] a = new int[6];
		
		a[0] = 36;
		a[1] = 28;
		a[2] = 10;
		a[3] = 8;
		a[4] = 17;
		a[5] = 33;
		
		for (int i = 0; i < a.length; i++) {
			System.out.println(a[i]);
		}	
		
	}

}
