package day1006;

public class CharTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String data = "hello";
		char ch = data.charAt(0);
		
		System.out.println(ch);
		
		
		if (ch == 'h') {
			System.out.println("OK");
		} else {
			System.out.println("NO");
		}
	}

}
